import jsonPlaceHolder from "../apis/jsonPlaceHolder";

export const fetchPosts = () => {
  return function (dispatch, getState) {
    const promise = jsonPlaceHolder.get("/posts");

    
    dispatch({type: "FETCH_POSTS",
    payload: promise,})    
  };
};
