import React, { useState, useEffect } from "react";
import youtube from "../api/Youtube";

const UseVideos = (defaultSearchText) => {
  const [responseVideos, setResponseVideos] = useState([]);

  useEffect(() => {
    handleSubmit(defaultSearchText);
  }, []);

  const handleSubmit = async (searchText) => {
    const response = await youtube.get("/search", {
      params: {
        part: "snippet",
        type: "video",
        maxResults: "10",
        key: KEY,
        q: searchText,
      },
    });

    setResponseVideos(response.data.items);
    setselectedVideo(response.data.items[0]);
  };

  return [responseVideos, handleSubmit]
};

export default UseVideos;
