import React, { useState } from "react";
import Accordion from "./Accordion";
import WikiSearch from "./WikiSearch";
import ColorDropDown from "./ColorDropDown";
import TranslateWidget from "./TranslateWidget";
import Route from "./Route";

const items = [
  {
    title: "What is React",
    content:
      "Cafasfjadfjadfhlkadflkadfjladhfl adsfadsfafadf adfafadf adfadfadfadfeewr ",
  },
  {
    title: "Check React Hooks",
    content:
      "erioefoinwrvonsfvbnsofghopriggv oihofi ihefpiwejfp pjhpfweidfhwoeifh iheoi hfwefh",
  },
  {
    title: "What is Accordion",
    content:
      "efiwefoihwref hwgwrg wrg wefwefwefwerwerf er wqer weughiuh iugbsdkvsdvkbqef ",
  },
];

const rareElements = [
  {
    label: "Sapphire",
    value: "blue",
  },
  {
    label: "Gold",
    value: "yellow",
  },
  {
    label: "Ruby",
    value: "red",
  },
  {
    label: "Emerald",
    value: "green",
  },
];

const showAccordion = () => {
  if (window.location.pathname === "/") {
    return <Accordion items={items} />;
  }
};

const showWikiSearch = () => {
  if (window.location.pathname === "/wikisearch") {
    return <WikiSearch />;
  }
};

const showColorDropdown = () => {
  const [dropdownSelected, setDropdownSelected] = useState(rareElements[0]);
  if (window.location.pathname === "/dropdown") {
    return (
      <ColorDropDown
        label="Select a color"
        rareElements={rareElements}
        dropdownSelected={dropdownSelected}
        handledropdownSelected={setDropdownSelected}
      />
    );
  }
};

const showTranslate = () => {
  if (window.location.pathname === "/translate") {
    return <TranslateWidget />;
  }
};

export default () => {
  return (
    <div>
      <Route path="/">
      <Accordion items={items} />
      </Route>
      <Route path="/">
      <Accordion items={items} />
      </Route>
      <Route path="/">
      <Accordion items={items} />
      </Route>
      <Route path="/">
      <Accordion items={items} />
      </Route>
    </div>
  );
};
