import React from "react";

const getSeason = (lat, month) => {
  if (month > 2 && month < 9) {
    return lat < 0 ? "summer" : "winter";
  } else {
    return lat > 0 ? "summer" : "winter";
  }
};

const SeasonDisplay = (props) => {
    const season = this.getSeason(props.lat, new Date().getMonth());
