import React, { useState, useEffect } from "react";
import SearchBar from "./SearchBar";
import youtube from "../api/Youtube";
import SuggVideoList from "./SuggVideoList";
import MainVideo from "./MainVideo";

const KEY = "AIzaSyC6yphgB1k6vdqwEm8E984eI_1k6B7C4WM";

const AppTube = () => {
  const [responseVideos, setResponseVideos] = useState([]);
  const [selectedVideo, setselectedVideo] = useState(null);

  const handleVideoSelect = (video) => {
   ;
  };

  useEffect(() => {
    handleSubmit("Cricket");
  }, []);

  const handleSubmit = async (searchText) => {
    const response = await youtube.get("/search", {
      params: {
        part: "snippet",
        type: "video",
        maxResults: "10",
        key: KEY,
        q: searchText,
      },
    });

    setResponseVideos(response.data.items);
    setselectedVideo(response.data.items[0]);
  };

  return (
    <div className="ui container">
      <SearchBar handleSubmit={handleSubmit} />
      <div className="ui grid">
        <div className="ui row">
          <div className="eleven wide column">
            <MainVideo video={selectedVideo} />
          </div>
          <div className="five wide column">
            <SuggVideoList
              videos={responseVideos}
              onVideoSelect={(vide)=> setselectedVideo(video)}
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default AppTube;
