  }

  render() {
    return (
    <div>
        <form onSubmit={this.handleFormSubmit}>
      <input
        value={this.state.text}
        onChange={(e) => this.setState({ searchText: e.target.value })}
        type="text"
      />
      </form>
      </div>
    );
  }
}

export default SearchBar;
