import React from "react";
import SearchBar from "./SearchBar";
import youtube from "../api/Youtube";
import SuggVideoList from "./SuggVideoList";

const KEY = "AIzaSyC6yphgB1k6vdqwEm8E984eI_1k6B7C4WM";

class App extends React.Component {
  state = { responseVideos: [] };

  handleVideoSelect =()=>{
    
  }

  handleSubmit = async (searchText) => {
    const response = await youtube.get("/search", {
      params: {
        part: "snippet",
        type: "video",
        maxResults: "10",
        key: KEY,
        q: searchText,
      },
    });

    this.setState({ responseVideos: response.data.items });
  };

  render() {
    return (
      <div className="ui container">
        <SearchBar handleSubmit={this.handleSubmit} />
        <SuggVideoList videos={this.state.responseVideos} />I have total{" "}
        {this.state.responseVideos.length} videos
      </div>
    );
  }
}

export default App;
