import React, { useState, useEffect } from "react";
import Axios from "axios";

const Convert = ({ language, text }) => {
  useEffect(() => {
    Axios.post(
      "https://translation.googleapis.com/language/translate/v2",
      {},
      {
        params: {
          q: "",
          target: "",
          key
        },
      }
    );
  }, [language, text]);

  return <div></div>;
};

export default Convert;
